import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Login.scss'; // Import your custom styles here


const Login = () => {
    const navigate = useNavigate();

    const [formData, setFormData] = useState({
        email: '',
        password: '',
    });

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Implement your login logic here

            console.log('Login successful'); // This line simulates successful login. Replace with actual login logic

            // After successful login, navigate to the user profile page
            navigate('/profile');
            
        } catch (error) {
            console.error('Login error:', error);
            // Handle login error here
        }
    };

    return (
        <div className="login-container">
            <div className="login-card">
                <h2>Login to Your Account</h2>
                <form onSubmit={handleSubmit}>
                    <div className="form-group">
                        <label>Email</label>
                        <input
                            type="email"
                            name="email"
                            value={formData.email}
                            onChange={handleInputChange}
                            required
                        />
                    </div>
                    <div className="form-group">
                        <label>Password</label>
                        <input
                            type="password"
                            name="password"
                            value={formData.password}
                            onChange={handleInputChange}
                            required
                        />
                    </div>
                    <button type="submit" className="login-button">Login</button>
                </form>
                <p className="signup-link">
                    Don't have an account? <Link to="/register">Sign up</Link>
                </p>
            </div>
        </div>
    );
};

export default Login;
