import React, { useState } from 'react';
import axios from 'axios';
import './Register.scss';

const Register = () => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    confirmPassword: '',
    agreeToTerms: false,
  });

  const [feedbackMessage, setFeedbackMessage] = useState(''); // Added this state for feedback

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleCheckboxChange = (e) => {
    const { name, checked } = e.target;
    setFormData({
      ...formData,
      [name]: checked,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/api/users/register', formData);
      console.log('Registration successful:', response.data);
      setFeedbackMessage('Registration successful! Please check your email for verification link.'); // Feedback on success
    } catch (error) {
      console.error('Registration error:', error);
      setFeedbackMessage('Registration failed. Please try again.'); // Feedback on error
    }
  };

  return (
    <div className="register-container">
      <h2 className="register-title">Join Lotus Love Linkss Asia Today</h2>
      {feedbackMessage && <div className="feedback-message">{feedbackMessage}</div>} {/* Display feedback */}
      <form className="register-form" onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="username">Username</label>
          <input
            type="text"
            id="username"
            name="username"
            value={formData.username}
            onChange={handleInputChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="email">Email</label>
          <input
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleInputChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            name="password"
            value={formData.password}
            onChange={handleInputChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="confirmPassword">Confirm Password</label>
          <input
            type="password"
            id="confirmPassword"
            name="confirmPassword"
            value={formData.confirmPassword}
            onChange={handleInputChange}
            required
          />
        </div>
        <div className="form-group">
          <input
            type="checkbox"
            id="agreeToTerms"
            name="agreeToTerms"
            checked={formData.agreeToTerms}
            onChange={handleCheckboxChange}
            required
          />
          <label htmlFor="agreeToTerms">I agree to the terms and conditions</label>
        </div>
        <button className="register-button" type="submit">Register</button>
      </form>
    </div>
  );
};

export default Register;
