import React, { useState } from 'react';
import axios from 'axios';
import './Register.scss';

const Register = () => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    confirmPassword: '',
    agreeToTerms: false,
  });

  const [feedbackMessage, setFeedbackMessage] = useState('');
  const [isError, setIsError] = useState(false);  // Determine whether the feedback is an error or not

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleCheckboxChange = (e) => {
    const { name, checked } = e.target;
    setFormData(prev => ({ ...prev, [name]: checked }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (formData.password !== formData.confirmPassword) {
      setIsError(true);
      setFeedbackMessage('Passwords do not match.');
      return;
    }

    try {
      const response = await axios.post('/api/users/register', formData);
      setIsError(false);
      console.log('Registration successful:', response.data);
      setFeedbackMessage('Registration successful! Please check your email for verification.');
    } catch (error) {
      setIsError(true);
      console.error('Registration error:', error);
      const message = error.response && error.response.data && error.response.data.message
        ? error.response.data.message
        : 'Registration failed. Please try again.';
      setFeedbackMessage(message);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <div className="p-6 bg-white w-96 shadow-md rounded-xl">
        {feedbackMessage && <div className={`feedback-message mb-4 text-center ${isError ? 'text-red-600' : 'text-green-600'}`}>{feedbackMessage}</div>}
        
        <form onSubmit={handleSubmit}>
          {/* ... The rest of your form fields remain unchanged ... */}

          {/* Register Button */}
          <button type="submit" className="w-full bg-blue-600 text-white py-2 rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2">Register</button>
        </form>
      </div>
    </div>
  );
};

export default Register;
