import React from 'react';
import { Link } from 'react-router-dom';
import './LandingPage.scss';
import FooterComponent from '../Footer/FooterComponent';  // Adjust the path according to your folder structure

const LandingPage = () => {
  return (
    <div className="bg-gray-100 flex flex-col min-h-screen">  {/* Ensuring minimum height */}
      <div className="flex-1"> {/* This makes sure that the content takes up all available space, pushing the footer to the bottom */}
        <div className="hero-section">
          <h1 className="text-6xl text-lightblue mb-0">Lotus Love Links</h1>
          
          <div className="landing-buttons mt-16">
            <Link to="/login" className="landing-link">
              <button className="landing-button">Login</button>
            </Link>
            <Link to="/register" className="landing-link">
              <button className="landing-button">Register</button>
            </Link>
          </div>
          
          
        </div>

        <div className="trusted-banner py-5">
          <div className="statement-column">Review your matches for free</div>
          <div className="statement-column">Most advanced messaging features</div>
          <div className="statement-column">Advanced security for a more enjoyable experience</div>
          <div className="statement-column">Find your soulmate right now!</div>
        </div>

        <div className="Lotus Love Linkss-story-section py-10">
          <div className="left-section">
            <h2 className="font-bold text-5xl mb-8">Why Choose Lotus Love Linkss Asia?</h2>
            <div className="story-content">
              <h3 className="text-4xl font-bold mb-4">A Legacy of Connection:</h3>
              <p className="mb-8">Since 2023, Lotus Love Linkss Asia has stood as a trusted name in Asian dating.</p>
            </div>
          </div>
          {/* ... right-section ... */}
        </div>
      </div>

      <FooterComponent />  {/* Footer at the bottom */}
    </div>
  );
};

export default LandingPage;
